import React, { useState, useEffect } from "react";
import InputTask from "./components/InputTask";
import styled from "styled-components";

function App() {
  const [taskList, setTaskList] = useState([]);
  //const [checkAdd, setCheckAdd] = useState("");

  useEffect(() => {
    preSet();
  }, []);

  const preSet = () => {
    setTimeout(() => {
      // Retrieve data from localStorage and parse it
      // const items = JSON.parse(
      //   localStorage.getItem("yourLocalStorageKey") || "{}"
      // );
      const items = { ...localStorage };

      // Convert the object to an array of key-value pairs
      const result = Object.entries(items);

      setTaskList(result);
      console.log(result); // Log the result instead of taskList
    }, 2000);
  };

  return (
    <>
      <h1>TaskBook</h1>
      <InputTask refreshList={preSet} />
      <TaskListHeader>
        <h1>List of All Tasks</h1>
        <select>
          <option>Sort Tasks - All</option>
          <option value={true}>Completed Tasks</option>
          <option value={false}>Incompleted Tasks</option>
        </select>
      </TaskListHeader>
      <TaskContainer>
        {/* {taskList.map((task) => {
          //console.log(task);
        })} */}
      </TaskContainer>
    </>
  );
}

const TaskContainer = styled.div`
  margin: 1rem;
  height: 30rem;
  background-color: rgb(13, 6, 65);
  border-radius: 0.3rem;
`;

const TaskListHeader = styled.div`
  display: flex;
  h1 {
    margin: 1rem;
  }

  select {
    margin: 1rem;
  }
`;

export default App;
