import React, { useState, useEffect } from "react";
import InputTask from "./components/InputTask";
import styled from "styled-components";

function App() {
  const [taskList, setTaskList] = useState([]);
  const [checkAdd, setCheckAdd] = useState("");

  useEffect(() => {
    const items = { ...localStorage };
    const result = Object.keys(items).map((key) => [key, items[key]]);
    setTaskList(result);
  }, [checkAdd]);

  return (
    <>
      <h1>TaskBook</h1>
      <InputTask refreshList={setCheckAdd} />
      <TaskListHeader>
        <h1>List of All Tasks</h1>
        <select>
          <option>Sort Tasks - All</option>
          <option value={true}>Completed Tasks</option>
          <option value={false}>Incompleted Tasks</option>
        </select>
      </TaskListHeader>
      <TaskContainer>
        {taskList.map((task) => {
          console.log(task);
        })}
      </TaskContainer>
    </>
  );
}

const TaskContainer = styled.div`
  margin: 1rem;
  height: 30rem;
  background-color: rgb(13, 6, 65);
  border-radius: 0.3rem;
`;

const TaskListHeader = styled.div`
  display: flex;
  h1 {
    margin: 1rem;
  }

  select {
    margin: 1rem;
  }
`;

export default App;
