import React, { useState, useEffect } from "react";
import InputTask from "./components/InputTask";
import styled from "styled-components";

function App() {
  const [taskVal, setTaskVal] = useState("");

  return (
    <>
      <h1>TaskBook</h1>
      <InputTask setVal={setTaskVal} />
      <TaskListHeader>
        <h1>List of All Tasks</h1>
        <select>
          <option>Sort Tasks - All</option>
          <option value={true}>Completed Tasks</option>
          <option value={false}>Incompleted Tasks</option>
        </select>
      </TaskListHeader>
      <TaskContainer>{taskVal}</TaskContainer>
    </>
  );
}

const TaskContainer = styled.div`
  margin: 1rem;
  height: 30rem;
  background-color: rgb(13, 6, 65);
  border-radius: 0.3rem;
`;

const TaskListHeader = styled.div`
  display: flex;
  h1 {
    margin: 1rem;
  }

  select {
    margin: 1rem;
  }
`;

export default App;
