import React from "react";
import styled from "styled-components";

function DispTask({ task }) {
  return (
    <>
      <Container>
        <input type="checkbox" id="tick" />
        <div id="content">{task.content}</div>
        <button id="del-task">Delete Task</button>
        <button id="edit-task">Edit Task</button>
      </Container>
    </>
  );
}

const Container = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: rgb(13, 6, 65);
  #content {
    color: white;
    font-size: 1.2rem;
  }

  #tick {
    width: 2rem;
    height: 2rem;
    clip-path: circle(40% at 50% 50%);
    margin-left: 1rem;
  }

  #del-task,
  #edit-task {
    width: 10rem;
    height: 3rem;
    background-color: cadetblue;
    color: rgb(13, 6, 65);
    margin: 1rem;
    padding: 0.5rem;
    border: none;
    border-radius: 0.5rem;
    font-size: 1.2rem;
  }
`;

export default DispTask;
