import React from "react";
import styled from "styled-components";

function InputTask() {
  return (
    <>
      <Container>
        <h2>Write Task here : </h2>
        <textarea id="text-in" cols={40} rows={5}></textarea>
        <button id="add-btn">Add Task</button>
        <button id="delAll-btn">Delete All Task</button>
      </Container>
    </>
  );
}

const Container = styled.div`
  background-color: lightblue;
  display: flex;
  align-items: center;
  #text-in {
    border-radius: 0.7rem;
    font-size: 1.2rem;
    border: 3px solid black;
    margin: 1rem;
  }
  #add-btn,
  #delAll-btn {
    width: 10rem;
    height: 3rem;
    background-color: cornflowerblue;
    color: rgb(10, 1, 20);
    margin: 1rem;
    padding: 0.5rem;
    border: none;
    border-radius: 0.5rem;
    font-size: 1.2rem;
  }
`;

export default InputTask;
