import React, { useState, useEffect } from "react";
import InputTask from "./components/InputTask";
import styled from "styled-components";
import DispTask from "./components/DispTask";

function App() {
  const [taskList, setTaskList] = useState([]);

  useEffect(() => {
    preSet();
  }, []);

  useEffect(() => {
    console.log(taskList);
  }, [taskList]);

  const preSet = () => {
    const items = { ...localStorage };
    const result = Object.entries(items);
    setTaskList(result);
  };

  return (
    <>
      <h1>TaskBook</h1>
      <InputTask refreshList={preSet} />
      <TaskListHeader>
        <h1>List of All Tasks</h1>
        <select>
          <option>Sort Tasks - All</option>
          <option value={true}>Completed Tasks</option>
          <option value={false}>Incompleted Tasks</option>
        </select>
      </TaskListHeader>
      <TaskContainer>
        {/* {taskList.map((task) => {
          //console.log(task);
        })} */}
        <DispTask
          task={{
            content:
              "Hi my name is himanshu i am working at edelta at ahmedabad",
            time: "18/12/2023 17:12:34",
          }}
        />
        <DispTask
          task={{
            content: "i am himanshu",
            time: "18/12/2023 17:12:34",
          }}
        />
        <DispTask
          task={{
            content:
              "Hi my name is himanshu i am working at edelta at ahmedabad . i learn react here . dsdjkfdd  sdfdfsdi   sdfiudfsdifd  sdfuihdfhdsf   sdfhdfdsfhd   dhsd",
            time: "18/12/2023 17:12:34",
          }}
        />
      </TaskContainer>
      <Footer>
        <div></div>
        <label id="lb1">Added At </label>
        <label id="lb2">Task</label>
      </Footer>
    </>
  );
}

const TaskContainer = styled.div`
  margin: 1rem;
  height: 35rem;
  background-color: black; //rgb(13, 6, 65);
  border-radius: 0.3rem;
`;

const TaskListHeader = styled.div`
  display: flex;
  align-items: center;
  h1 {
    margin: 1rem;
  }

  select {
    margin: 1rem;
  }
`;

const Footer = styled.div`
  display: grid;
  grid-template-columns: 14% 20% 14% 52%;
  align-items: center;

  label {
    color: white;
    font-size: 1.8rem;
    width: 10rem;
    height: 3rem;
    background-color: rgb(13, 6, 65);
    text-align: center;
    border-bottom-left-radius: 1rem;
    border-bottom-right-radius: 1rem;
  }

  div {
    grid: 1/2;
  }

  #lb1 {
    grid: 2/3;
  }

  #lb2 {
    grid: 3/4;
  }
`;
export default App;
